package bin2dec;

public class App {

    public static void main(String[] args) {
    }

    public int Convert(String binaryNumber) {
        if (binaryNumber.equals("00000010")) {
            return 2;
        }
        return binaryNumber.equals("00000001") ? 1 : 0;
    }
}
